import React, { PropTypes } from 'react';
import { getLocaleById, getText } from './lang';

class DonateButton extends React.Component {

    constructor(props, context) {
        super(props, context);
        this._ = getText.bind(this, getLocaleById(this.context.locale));
        this.state = {
            text: this._('Donate')
        };
        this.changeText = this.changeText.bind(this);
    }

    changeText() {
        this.setState({
            text: this._('Redirecting to paypal...')
        });
    }

    render() {
        return (
            <form action='https://www.paypal.com/cgi-bin/webscr' method='post' target='_top'>
                <input type='hidden' name='cmd' value='_s-xclick' />
                <input type='hidden' name='encrypted' value='-----BEGIN PKCS7----------END PKCS7-----' />
                <input type='submit' value={this.state.text} onClick={this.changeText} id='donateBtn' className='btn btn-success btn-sm btn--donate' />
                <img alt='' border='0' src='https://www.paypalobjects.com/en_US/i/scr/pixel.gif' width='1' height='1' />
            </form>
        )
    }

}

DonateButton.childContextTypes = {
    locale: PropTypes.string
}

export default DonateButton
